import './Learning.css';
import { useState } from "react";

const Learning = ({flashcards, onExit, onResult}) => {
  console.log(flashcards)
  const [currentIdx, setCurrentIdx] = useState(0);
  const [showAnswer,setShowAnswer] = useState(false);
  const handleNext = () =>{
    setShowAnswer(false);
    if( currentIdx+1 < flashcards.length ){
      setCurrentIdx(currentIdx+1);
    } else {
      // alert("학습완료!");
      // onExit();
      onResult();
    }
  }
  return (
    <div className="learning">
      <h2>플래시카드 학습</h2>
      <h3>질문</h3>
      <p> {flashcards[currentIdx].question}</p>
      <h3>답변</h3>
      {
        showAnswer &&
        <p>{flashcards[currentIdx].answer}</p>
      }
      <div className='btn'>
        <button 
          onClick={()=>{setShowAnswer(!showAnswer)}}>
            {
              showAnswer ? "답변닫기" : "답변보기"
            }
          </button>
        <button onClick={handleNext}>다음카드</button>

      </div>
      <button onClick={onExit}>학습종료</button>
    </div>
  );
};

export default Learning;
