import './Result.css';
import confetti from 'canvas-confetti';

const Result = ({ onExit }) => {
  const triggerConfetti = () => {
    // 폭죽 효과 실행
    confetti({
      particleCount: 100,
      spread: 70,
      origin: { y: 0.6 }, // 폭죽 시작 위치
    });
  };

  const handleExit = () => {
    triggerConfetti(); // 버튼 클릭 시 폭죽 실행
    setTimeout(onExit, 2000); // 2초 후 "처음으로 돌아가기" 실행
  };

  return (
    <div className="result">
      <h2>학습종료</h2>
      <p>수고하셨습니다 :）</p>
      <img src="./pang.png" alt="picture1" height="300px" width="300px" />
      <button onClick={handleExit}>처음으로 돌아가기</button>
    </div>
  );
};

export default Result;
